// Function Node
const sendData = async () => {
    try {
        const response = await fetch('https://directus.craftpodium.com/items/social_networks', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': 'Bearer 53Xre_uZuh6NX88L8Q_SF9zNreJX672T'
            },
            body: JSON.stringify({
                name: Inputs.name,
                api_credentials: {
                    api_key: Inputs.api
                }
            })
        });
        
        const data = await response.json();
        console.log('Response:', data);
        
        if (response.ok) {
            Outputs.success();  // Сигнал успеха
            Outputs.data = data;
        } else {
            Outputs.error();  // Сигнал ошибки
            Outputs.errorData = data;
        }
    } catch(e) {
        console.error('Request failed:', e);
        Outputs.error();  // Сигнал ошибки
        Outputs.errorData = e.message;
    }
}

// Запускаем при получении сигнала Run
sendData();

