async function refreshAccessToken() {
    let refreshToken = localStorage.getItem("jwt_refresh_token");

    if (!refreshToken) {
        console.error("‚ùå –û—à–∏–±–∫–∞: refresh token –æ—Ç—Å—É—Ç—Å—Ç–≤—É–µ—Ç! –ù—É–∂–Ω–æ —Å–Ω–æ–≤–∞ –∞–≤—Ç–æ—Ä–∏–∑–æ–≤–∞—Ç—å—Å—è.");
        window.location.replace("/login");
        return;
    }

    try {
        const response = await fetch("https://directus.nplanner.ru/auth/refresh", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ refresh_token: refreshToken })
        });

        const data = await response.json();

        if (response.ok && data.data?.access_token) {
            console.log("‚úÖ Access token –æ–±–Ω–æ–≤–ª—ë–Ω –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏!");

            localStorage.setItem("jwt_access_token", data.data.access_token);
            localStorage.setItem("jwt_refresh_token", data.data.refresh_token);
            localStorage.setItem("jwt_expires", Date.now() + data.data.expires);

            return data.data.access_token;
        } else {
            console.error("‚ùå –û—à–∏–±–∫–∞ –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è —Ç–æ–∫–µ–Ω–∞:", data);
            window.location.replace("/logout");
        }
    } catch (error) {
        console.error("‚ùå –û—à–∏–±–∫–∞ —Å–µ—Ç–∏ –ø—Ä–∏ –æ–±–Ω–æ–≤–ª–µ–Ω–∏–∏ —Ç–æ–∫–µ–Ω–∞:", error);
        window.location.replace("/logout");
    }
}

if (window.Worker) {
    let currentPath = window.location.pathname;

    // üîÑ –ù–∞ —Å—Ç—Ä–∞–Ω–∏—Ü–µ /login –ø—Ä–æ–≤–µ—Ä–∫—É —Ç–æ–∫–µ–Ω–∞ –Ω–µ –≤—ã–ø–æ–ª–Ω—è–µ–º
    if (currentPath === "/login") {
        console.log("üîπ –ù–∞—Ö–æ–¥–∏–º—Å—è –Ω–∞ /login, –ø—Ä–æ–≤–µ—Ä–∫–∞ —Ç–æ–∫–µ–Ω–∞ –Ω–µ —Ç—Ä–µ–±—É–µ—Ç—Å—è.");

        const worker = new Worker("/worker.js");
        worker.postMessage("start");

        worker.addEventListener("message", (event) => {
            if (event.data === "logout") {
                console.log("üî¥ Web Worker –æ–±–Ω–∞—Ä—É–∂–∏–ª –Ω–µ–≤–∞–ª–∏–¥–Ω—ã–π —Ç–æ–∫–µ–Ω, –ø–µ—Ä–µ–Ω–∞–ø—Ä–∞–≤–ª—è–µ–º –Ω–∞ /logout...");
                setTimeout(() => {
                    window.location.replace("/logout");
                }, 500);
            }
        });

        console.log("‚úÖ Web Worker –∑–∞–ø—É—â–µ–Ω –Ω–∞ —Å—Ç—Ä–∞–Ω–∏—Ü–µ /login, –Ω–æ –±–µ–∑ –ø—Ä–æ–≤–µ—Ä–∫–∏ —Ç–æ–∫–µ–Ω–∞.");
        
        setTimeout(() => {
            Outputs.Mounted = true;
        }, 200);

        return;
    }

    // üìå –ü—Ä–æ–≤–µ—Ä—è–µ–º —Ç–æ–∫–µ–Ω—ã –ø–µ—Ä–µ–¥ –∑–∞–ø—É—Å–∫–æ–º Web Worker
    Outputs.Mounted = false; // –°–∫—Ä—ã–≤–∞–µ–º UI –ø–µ—Ä–µ–¥ –ø—Ä–æ–≤–µ—Ä–∫–æ–π

    let accessToken = localStorage.getItem("jwt_access_token");
    let refreshToken = localStorage.getItem("jwt_refresh_token");
    let expiresAt = parseInt(localStorage.getItem("jwt_expires") || "0", 10);

    if (!accessToken || !refreshToken) {
        console.log("‚ùå –¢–æ–∫–µ–Ω—ã –æ—Ç—Å—É—Ç—Å—Ç–≤—É—é—Ç! –ü–µ—Ä–µ–Ω–∞–ø—Ä–∞–≤–ª—è–µ–º –Ω–∞ /login");
        setTimeout(() => {
            window.location.replace("/login");
        }, 500);
        return;
    }

    let timeLeft = expiresAt - Date.now();
    if (timeLeft < 60000) {
        console.log("‚è≥ –¢–æ–∫–µ–Ω —Å–∫–æ—Ä–æ –∏—Å—Ç–µ—á—ë—Ç, –∑–∞–ø—É—Å–∫–∞–µ–º –æ–±–Ω–æ–≤–ª–µ–Ω–∏–µ...");
        refreshAccessToken();
    }

    // üîÑ –ó–∞–ø—É—Å–∫–∞–µ–º Web Worker –∏ –ø–µ—Ä–µ–¥–∞—ë–º refreshToken
    const worker = new Worker("/worker.js");
    worker.postMessage({ type: "start", refreshToken });

    worker.addEventListener("message", (event) => {
        if (event.data === "logout") {
            console.log("üî¥ Web Worker –æ–±–Ω–∞—Ä—É–∂–∏–ª –Ω–µ–≤–∞–ª–∏–¥–Ω—ã–π —Ç–æ–∫–µ–Ω, –ø–µ—Ä–µ–Ω–∞–ø—Ä–∞–≤–ª—è–µ–º –Ω–∞ /logout...");
            setTimeout(() => {
                window.location.replace("/logout");
            }, 500);
        }

        if (event.data.type === "tokenUpdated") {
            console.log("‚úÖ Web Worker –æ–±–Ω–æ–≤–∏–ª —Ç–æ–∫–µ–Ω—ã!");

            localStorage.setItem("jwt_access_token", event.data.accessToken);
            localStorage.setItem("jwt_refresh_token", event.data.refreshToken);
            localStorage.setItem("jwt_expires", event.data.expires);

            worker.postMessage({ type: "updateToken", refreshToken: event.data.refreshToken });
        }
    });

    console.log("‚úÖ Web Worker –∑–∞–ø—É—â–µ–Ω, —Ç–æ–∫–µ–Ω –±—É–¥–µ—Ç –æ–±–Ω–æ–≤–ª—è—Ç—å—Å—è –≤ —Ñ–æ–Ω–µ!");

    setTimeout(() => {
        Outputs.Mounted = true;
        console.log("üü¢ UI –≤–∫–ª—é—á—ë–Ω –ø–æ—Å–ª–µ –ø—Ä–æ–≤–µ—Ä–∫–∏ —Ç–æ–∫–µ–Ω–∞");
    }, 200);
} else {
    console.error("‚ùå –í–∞—à –±—Ä–∞—É–∑–µ—Ä –Ω–µ –ø–æ–¥–¥–µ—Ä–∂–∏–≤–∞–µ—Ç Web Workers.");
}

fetch("https://directus.nplanner.ru/auth/login", {
    method: "POST",
    headers: {
        "Content-Type": "application/json",
        "Accept": "application/json"
    },
    body: JSON.stringify({
        email: Inputs.Email,
        password: Inputs.Password
    })
})
.then(response => response.json())  // –ü—Ä–µ–æ–±—Ä–∞–∑—É–µ–º –æ—Ç–≤–µ—Ç –≤ JSON
.then(async (data) => {
    console.log("–û—Ç–≤–µ—Ç Directus:", data);

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º, –µ—Å—Ç—å –ª–∏ —Ç–æ–∫–µ–Ω—ã
    if (data.data?.access_token && data.data?.refresh_token) {
        const accessToken = data.data.access_token;
        const refreshToken = data.data.refresh_token;
        const expiresInMs = data.data.expires;

        // –°–æ—Ö—Ä–∞–Ω—è–µ–º —Ç–æ–∫–µ–Ω—ã –≤ localStorage
        localStorage.setItem("jwt_access_token", accessToken);
        localStorage.setItem("jwt_refresh_token", refreshToken);
        localStorage.setItem("jwt_expires", Date.now() + expiresInMs);

        // –ó–∞–ø—Ä–æ—Å –Ω–∞ –ø–æ–ª—É—á–µ–Ω–∏–µ –¥–∞–Ω–Ω—ã—Ö –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
        try {
            const userResponse = await fetch("https://directus.nplanner.ru/users/me", {
                method: "GET",
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": "Bearer " + accessToken
                }
            });

            const userData = await userResponse.json();

            if (userResponse.ok && userData.data) {
                console.log("‚úÖ –î–∞–Ω–Ω—ã–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è —É—Å–ø–µ—à–Ω–æ –ø–æ–ª—É—á–µ–Ω—ã!");

                // –°–æ—Ö—Ä–∞–Ω—è–µ–º –¥–∞–Ω–Ω—ã–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –≤ localStorage
                localStorage.setItem("user_id", userData.data.id);
                localStorage.setItem("user_first_name", userData.data.first_name || "");
                localStorage.setItem("user_last_name", userData.data.last_name || "");
                localStorage.setItem("user_email", userData.data.email || "");

                // –£—Å–ø–µ—à–Ω—ã–π –ª–æ–≥–∏–Ω + –ø–æ–ª—É—á–µ–Ω–∏–µ –¥–∞–Ω–Ω—ã—Ö –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
                Outputs.Success = true;
            } else {
                console.error("‚ùå –û—à–∏–±–∫–∞ –ø–æ–ª—É—á–µ–Ω–∏—è –¥–∞–Ω–Ω—ã—Ö –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è:", userData);
                Outputs.Error = "–û—à–∏–±–∫–∞ –ø–æ–ª—É—á–µ–Ω–∏—è –¥–∞–Ω–Ω—ã—Ö –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è";
            }
        } catch (error) {
            console.error("‚ùå –û—à–∏–±–∫–∞ —Å–µ—Ç–∏ –ø—Ä–∏ –ø–æ–ª—É—á–µ–Ω–∏–∏ –¥–∞–Ω–Ω—ã—Ö –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è:", error);
            Outputs.Error = "–û—à–∏–±–∫–∞ —Å–æ–µ–¥–∏–Ω–µ–Ω–∏—è –ø—Ä–∏ –ø–æ–ª—É—á–µ–Ω–∏–∏ –¥–∞–Ω–Ω—ã—Ö –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è";
        }
    } else {
        Outputs.Error = "–û—à–∏–±–∫–∞ –∞–≤—Ç–æ—Ä–∏–∑–∞—Ü–∏–∏: –¢–æ–∫–µ–Ω –Ω–µ –ø–æ–ª—É—á–µ–Ω";
    }
})
.catch(error => {
    console.error("–û—à–∏–±–∫–∞ –∑–∞–ø—Ä–æ—Å–∞:", error);
    Outputs.Error = "–û—à–∏–±–∫–∞ —Å–æ–µ–¥–∏–Ω–µ–Ω–∏—è —Å —Å–µ—Ä–≤–µ—Ä–æ–º";
});

