    console.error("‚ùå –û—à–∏–±–∫–∞: –ù–µ —É–∫–∞–∑–∞–Ω–æ –∫–ª—é—á–µ–≤–æ–µ —Å–ª–æ–≤–æ.");
    Outputs.error();
    return;
}

let accessToken = localStorage.getItem("jwt_access_token");

if (!accessToken) {
    console.error("‚ùå –û—à–∏–±–∫–∞: –¢–æ–∫–µ–Ω –æ—Ç—Å—É—Ç—Å—Ç–≤—É–µ—Ç!");
    Outputs.error();
    return;
}

// –û—Ç–ø—Ä–∞–≤–∫–∞ POST-–∑–∞–ø—Ä–æ—Å–∞ —Å keyword –∏ —Ç–æ–∫–µ–Ω–æ–º
fetch("https://n8n.nplanner.ru/webhook/4af3f3e9-aeec-4d31-933b-1e6e5ef68f93", {
    method: "POST",
    headers: {
        "Content-Type": "application/json"
    },
    body: JSON.stringify({
        jwt_token: accessToken,
        keyword: Inputs.keyword.trim()
    })
})
.then(response => {
    if (!response.ok) {
        throw new Error(`HTTP –æ—à–∏–±–∫–∞! –°—Ç–∞—Ç—É—Å: ${response.status}`);
    }
    return response.json();
})
.then(data => {
    console.log("‚úÖ –ü–æ–ª—É—á–µ–Ω–Ω—ã–π –æ—Ç–≤–µ—Ç:", data); // –õ–æ–≥–∏—Ä—É–µ–º –æ—Ç–≤–µ—Ç –≤ –∫–æ–Ω—Å–æ–ª—å

    if (!Array.isArray(data)) {
        console.error("‚ùå –û—à–∏–±–∫–∞: –ù–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç –æ—Ç–≤–µ—Ç–∞", data);
        Outputs.error();
        return;
    }

    // –ü—Ä–µ–æ–±—Ä–∞–∑—É–µ–º –¥–∞–Ω–Ω—ã–µ –≤ –º–∞—Å—Å–∏–≤ –æ–±—ä–µ–∫—Ç–æ–≤ –¥–ª—è Noodl
    let formattedData = data.map((item, index) => ({
        id: item.phrase,
        phrase: item.phrase,
        number: item.number,
        Mounted: true
    }));

    // –ü–µ—Ä–µ–¥–∞—ë–º –¥–∞–Ω–Ω—ã–µ –≤ Outputs
    Outputs.data = formattedData;

    // –°–æ—Ö—Ä–∞–Ω—è–µ–º –≤ –ø–µ—Ä–µ–º–µ–Ω–Ω—É—é Noodl –¥–ª—è Repeater
    Noodl.Variables.ResponseData = formattedData;

    // –í—ã–∑—ã–≤–∞–µ–º —Å–∏–≥–Ω–∞–ª —É—Å–ø–µ—Ö–∞
    Outputs.success();
})
.catch(error => {
    console.error("‚ùå Fetch error:", error);
    Outputs.error();
});

// –ü–æ–ª—É—á–∞–µ–º –Ω–æ–≤—ã–µ –¥–∞–Ω–Ω—ã–µ –∏–∑ API
let allData = Inputs.data || [];

// –ü–æ–ª—É—á–∞–µ–º —Å–ø–∏—Å–æ–∫ –≤—ã–±—Ä–∞–Ω–Ω—ã—Ö –∫–ª—é—á–µ–π –∏–∑ `Inputs`
let selectedKeywords = Inputs.selectedKeywords || { items: [] };

// –ó–∞–≥—Ä—É–∂–∞–µ–º —Ä–∞–Ω–µ–µ —Å–æ—Ö—Ä–∞–Ω—ë–Ω–Ω—ã–µ –∫–ª—é—á–∏ –∏–∑ `localStorage`
let storedKeywords = localStorage.getItem("selectedKeywords");
let savedKeywords = storedKeywords ? JSON.parse(storedKeywords) : { items: [] };

// –£–±–µ–¥–∏–º—Å—è, —á—Ç–æ `selectedKeywords.items` –∏ `savedKeywords.items` ‚Äî —ç—Ç–æ –º–∞—Å—Å–∏–≤—ã
if (!Array.isArray(selectedKeywords.items)) {
    selectedKeywords.items = [];
}
if (!Array.isArray(savedKeywords.items)) {
    savedKeywords.items = [];
}

// üîπ –ü–æ–ª—É—á–∞–µ–º —Å–ø–∏—Å–æ–∫ ID –≤—ã–±—Ä–∞–Ω–Ω—ã—Ö –∫–ª—é—á–µ–π
let selectedIds = new Set(selectedKeywords.items.map(item => item.id));

// üîπ –§–∏–ª—å—Ç—Ä—É–µ–º `allData`, –æ—Å—Ç–∞–≤–ª—è—è —Ç–æ–ª—å–∫–æ –æ—Ç–º–µ—á–µ–Ω–Ω—ã–µ —ç–ª–µ–º–µ–Ω—Ç—ã
let filteredData = allData.filter(item => selectedIds.has(item.id));

// üîπ –û–±—ä–µ–¥–∏–Ω—è–µ–º –æ—Ç—Ñ–∏–ª—å—Ç—Ä–æ–≤–∞–Ω–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ —Å —É–∂–µ —Å–æ—Ö—Ä–∞–Ω—ë–Ω–Ω—ã–º–∏ –≤ `localStorage`
let mergedItems = [...savedKeywords.items, ...filteredData];

// üîπ –£–±–∏—Ä–∞–µ–º –¥—É–±–ª–∏–∫–∞—Ç—ã –ø–æ `id`
let updatedSelectedItems = mergedItems.filter((item, index, self) => 
    index === self.findIndex((t) => t.id === item.id)
);

// üîπ –ü—Ä–æ–≤–µ—Ä—è–µ–º, –∏–∑–º–µ–Ω–∏–ª–∏—Å—å –ª–∏ –¥–∞–Ω–Ω—ã–µ –ø–µ—Ä–µ–¥ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ–º (—á—Ç–æ–±—ã –∏–∑–±–µ–∂–∞—Ç—å –∑–∞—Ü–∏–∫–ª–∏–≤–∞–Ω–∏—è)
let hasChanged = JSON.stringify(savedKeywords.items) !== JSON.stringify(updatedSelectedItems);
if (hasChanged) {
    localStorage.setItem("selectedKeywords", JSON.stringify({ items: updatedSelectedItems }));
}

// üîπ –ó–∞–ø—É—Å–∫–∞–µ–º —Å–ª–µ–¥—É—é—â—É—é `Function`, –µ—Å–ª–∏ –¥–∞–Ω–Ω—ã–µ –∏–∑–º–µ–Ω–∏–ª–∏—Å—å
if (hasChanged) {
    Outputs.run();  // ‚úÖ –ó–∞–ø—É—Å–∫–∞–µ–º –≤—Ç–æ—Ä—É—é `Function` —Ç–æ–ª—å–∫–æ –ø—Ä–∏ –∏–∑–º–µ–Ω–µ–Ω–∏–∏ –¥–∞–Ω–Ω—ã—Ö
}

// –ó–∞–≥—Ä—É–∂–∞–µ–º `selectedKeywords` –∏–∑ `localStorage`
let storedKeywords = localStorage.getItem("selectedKeywords");
let savedKeywords = storedKeywords ? JSON.parse(storedKeywords) : { items: [] };

// –ï—Å–ª–∏ `localStorage` –ø—É—Å—Ç, `Repeater` –æ—Å—Ç–∞—ë—Ç—Å—è –ø—É—Å—Ç—ã–º
if (!Array.isArray(savedKeywords.items)) {
    savedKeywords.items = [];
}

// üîπ –û—Ç–ø—Ä–∞–≤–ª—è–µ–º —Å–æ—Ö—Ä–∞–Ω—ë–Ω–Ω—ã–µ –∫–ª—é—á–µ–≤—ã–µ —Å–ª–æ–≤–∞ –≤ `Repeater`
Outputs.filteredData = savedKeywords.items;

// –ü–æ–ª—É—á–∞–µ–º ID —É–¥–∞–ª—è–µ–º–æ–≥–æ –∫–ª—é—á–∞
let removeId = Inputs.removeId;

// –ó–∞–≥—Ä—É–∂–∞–µ–º —Ä–∞–Ω–µ–µ —Å–æ—Ö—Ä–∞–Ω—ë–Ω–Ω—ã–µ –∫–ª—é—á–∏ –∏–∑ `localStorage`
let storedKeywords = localStorage.getItem("selectedKeywords");
let savedKeywords = storedKeywords ? JSON.parse(storedKeywords) : { items: [] };

// –£–±–µ–¥–∏–º—Å—è, —á—Ç–æ `savedKeywords.items` ‚Äî —ç—Ç–æ –º–∞—Å—Å–∏–≤
if (!Array.isArray(savedKeywords.items)) {
    savedKeywords.items = [];
}

// üîπ –§–∏–ª—å—Ç—Ä—É–µ–º —Å–ø–∏—Å–æ–∫, –∏—Å–∫–ª—é—á–∞—è —ç–ª–µ–º–µ–Ω—Ç —Å `removeId`
let updatedSelectedItems = savedKeywords.items.filter(item => item.id !== removeId);

// üîπ –û–±–Ω–æ–≤–ª—è–µ–º `localStorage`
localStorage.setItem("selectedKeywords", JSON.stringify({ items: updatedSelectedItems }));

// üîπ –û—Ç–ø—Ä–∞–≤–ª—è–µ–º –æ–±–Ω–æ–≤–ª—ë–Ω–Ω—ã–π —Å–ø–∏—Å–æ–∫ –≤ `Repeater`
Outputs.filteredData = updatedSelectedItems;

let selectedItems = Noodl.Objects.selectedKeywords || { items: [] };

if (!Array.isArray(selectedItems.items)) {
    selectedItems.items = [];
}

let itemId = Inputs.itemId;
let phraseText = Inputs.phrase;

let index = selectedItems.items.findIndex(item => item.id === itemId);

if (Inputs.isChecked) {
    if (index === -1) {
        selectedItems.items.push({ id: itemId, phrase: phraseText });
    }
} else {
    if (index !== -1) {
        selectedItems.items.splice(index, 1);
    }
}

Noodl.Objects.selectedKeywords = selectedItems;
Outputs.selectedItems = selectedItems.items;

