const refreshAccessToken = async () => {
    let refreshToken = localStorage.getItem("jwt_refresh_token");

    if (!refreshToken) {
        console.error("‚ùå –û—à–∏–±–∫–∞: refresh token –æ—Ç—Å—É—Ç—Å—Ç–≤—É–µ—Ç! –ù—É–∂–Ω–æ —Å–Ω–æ–≤–∞ –∞–≤—Ç–æ—Ä–∏–∑–æ–≤–∞—Ç—å—Å—è.");
        window.location.replace("/login"); // –û—Ç–ø—Ä–∞–≤–ª—è–µ–º –Ω–∞ Login
        return;
    }

    try {
        const refreshResponse = await fetch("https://directus.nplanner.ru/auth/refresh", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ refresh_token: refreshToken })
        });

        const refreshData = await refreshResponse.json();

        if (refreshResponse.ok && refreshData.data?.access_token) {
            console.log("‚úÖ Access token –æ–±–Ω–æ–≤–ª—ë–Ω –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏!");

            localStorage.setItem("jwt_access_token", refreshData.data.access_token);
            localStorage.setItem("jwt_expires", Date.now() + refreshData.data.expires); // –û–±–Ω–æ–≤–ª—è–µ–º expires
        } else {
            console.error("‚ùå –û—à–∏–±–∫–∞ –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è —Ç–æ–∫–µ–Ω–∞:", refreshData);
            window.location.replace("/login"); // –†–∞–∑–ª–æ–≥–∏–Ω–∏–≤–∞–µ–º
        }
    } catch (error) {
        console.error("‚ùå –û—à–∏–±–∫–∞ —Å–µ—Ç–∏ –ø—Ä–∏ –æ–±–Ω–æ–≤–ª–µ–Ω–∏–∏ —Ç–æ–∫–µ–Ω–∞:", error);
        window.location.replace("/login"); // –†–∞–∑–ª–æ–≥–∏–Ω–∏–≤–∞–µ–º
    }
};

const checkAndRefreshToken = () => {
    let accessToken = localStorage.getItem("jwt_access_token");
    let expiresAt = parseInt(localStorage.getItem("jwt_expires") || "0", 10);
    let currentPath = window.location.pathname;

    // –ü–æ–∫–∞ –ø—Ä–æ–≤–µ—Ä–∫–∞ –∏–¥—ë—Ç, —Å–∫—Ä—ã–≤–∞–µ–º –≥–ª–∞–≤–Ω—ã–π UI
    Outputs.Mounted = false;

    // –ï—Å–ª–∏ —Ç–æ–∫–µ–Ω–∞ –Ω–µ—Ç ‚Äî —Ä–µ–¥–∏—Ä–µ–∫—Ç –Ω–∞ Login
    if (!accessToken && currentPath !== "/login") {
        console.log("‚ùå –¢–æ–∫–µ–Ω –æ—Ç—Å—É—Ç—Å—Ç–≤—É–µ—Ç, —Ä–µ–¥–∏—Ä–µ–∫—Ç –Ω–∞ Login");
        window.location.replace("/login");
        return;
    }

    // –ï—Å–ª–∏ —Ç–æ–∫–µ–Ω –µ—Å—Ç—å, –Ω–æ –æ–Ω —Å–∫–æ—Ä–æ –∏—Å—Ç–µ—á—ë—Ç ‚Üí –æ–±–Ω–æ–≤–ª—è–µ–º
    let timeLeft = expiresAt - Date.now();
    if (accessToken && timeLeft < 60000) {
        console.log("‚è≥ –¢–æ–∫–µ–Ω —Å–∫–æ—Ä–æ –∏—Å—Ç–µ—á—ë—Ç, –æ–±–Ω–æ–≤–ª—è–µ–º...");
        refreshAccessToken();
    }

    // –ï—Å–ª–∏ –Ω–∞ Login –∏ —Ç–æ–∫–µ–Ω –µ—Å—Ç—å ‚Üí —Ä–µ–¥–∏—Ä–µ–∫—Ç –Ω–∞ Home
    if (accessToken && currentPath === "/login") {
        console.log("‚úÖ –¢–æ–∫–µ–Ω –Ω–∞–π–¥–µ–Ω, —Ä–µ–¥–∏—Ä–µ–∫—Ç –Ω–∞ Home");
        window.location.replace("/home");
    } else {
        console.log("‚úÖ –¢–æ–∫–µ–Ω –Ω–∞–π–¥–µ–Ω, –æ—Å—Ç–∞—ë–º—Å—è –Ω–∞ —Ç–µ–∫—É—â–µ–π —Å—Ç—Ä–∞–Ω–∏—Ü–µ");
    }

    // –ü–æ—Å–ª–µ –ø—Ä–æ–≤–µ—Ä–∫–∏ —Ç–æ–∫–µ–Ω–∞ –ø–æ–∫–∞–∑—ã–≤–∞–µ–º UI
    setTimeout(() => {
        Outputs.Mounted = true;
    }, 200);
};

// üèÅ –ó–∞–ø—É—Å–∫–∞–µ–º –ø—Ä–æ–≤–µ—Ä–∫—É —Ç–æ–∫–µ–Ω–∞ –ø—Ä–∏ —Å—Ç–∞—Ä—Ç–µ
checkAndRefreshToken();

fetch("https://directus.nplanner.ru/auth/login", {
    method: "POST",
    headers: {
        "Content-Type": "application/json",
        "Accept": "application/json"
    },
    body: JSON.stringify({
        email: Inputs.Email,
        password: Inputs.Password
    })
})
.then(response => response.json())  // –ü—Ä–µ–æ–±—Ä–∞–∑—É–µ–º –æ—Ç–≤–µ—Ç –≤ JSON
.then(data => {
    console.log("–û—Ç–≤–µ—Ç Directus:", data);

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º, –µ—Å—Ç—å –ª–∏ access_token
    if (data.data?.access_token) {
        Outputs.AccessToken = data.data.access_token;
        Outputs.RefreshToken = data.data.refresh_token;
        Outputs.Expires = data.data.expires;
        Outputs.Success = true;
    } else {
        Outputs.Error = "–û—à–∏–±–∫–∞ –∞–≤—Ç–æ—Ä–∏–∑–∞—Ü–∏–∏";
        Outputs.Success = false;
    }
})
.catch(error => {
    console.error("–û—à–∏–±–∫–∞ –∑–∞–ø—Ä–æ—Å–∞:", error);
    Outputs.Error = "–û—à–∏–±–∫–∞ —Å–æ–µ–¥–∏–Ω–µ–Ω–∏—è —Å —Å–µ—Ä–≤–µ—Ä–æ–º";
    Outputs.Success = false;
});

let expiresInMs = Inputs.Expires;
if (Inputs.AccessToken && Inputs.RefreshToken) {
    localStorage.setItem("jwt_access_token", Inputs.AccessToken);
    localStorage.setItem("jwt_refresh_token", Inputs.RefreshToken);
    localStorage.setItem("jwt_expires", Date.now() + expiresInMs);
    Outputs.Success = true;
} else {
    Outputs.Success = false;
}

